package chapter01_ex;

public class exercise_09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double width = 5.3;
		double height = 8.6;
		
		double area = width * height;
    double perimeter = 2 * (width + height);
		
		System.out.println("The area of rectangle with width of 5.3 and height of 8.6 is " + area);
    System.out.println("The perimeter of rectangle with width of 5.3 and height of 8.6 is " + perimeter);
	}

}
